logic.code:
const si = inputs[0].getValue();
const clock = inputs[1].getValue();
const dir = inputs[2].getValue();
const rst = inputs[3].getValue();
if (rst) {
  state.data = 0;
} else if (clock && !state.lastClock) {
  if (dir) {
    state.data = ((state.data << 1) | (si ? 1 : 0)) & 0xFFFF;
  } else {
    state.data = (state.data >> 1) | (si ? 0x8000 : 0);
  }
}
state.lastClock = clock;
for (let i = 0; i < 16; i++) {
  outputs[i].setValue((state.data >> i) & 1);
}
outputs[16].setValue((state.data >> (dir ? 15 : 0)) & 1);
