for (let i = 0; i < 8; i++) {
  state.bits[i] = inputs[i].getValue();
}

rendering.customRender:
// Draw 8 LED indicators INSIDE the rounded rectangle
const ledSize = 8;
const spacing = 10;
const startY = -35; // Start at top, relative to center

for (let i = 7; i >= 0; i--) {
  const ledY = y + startY + (7 - i) * spacing;
  const isOn = state.bits[i];

  // LED circle
  ctx.fillStyle = isOn ? '#00ff00' : '#0a2a0a';
  ctx.strokeStyle = isOn ? '#00aa00' : '#1a3a1a';
  ctx.lineWidth = 1;
  ctx.beginPath();
  ctx.arc(x, ledY, ledSize/2, 0, Math.PI * 2);
  ctx.fill();
  ctx.stroke();

  // Bit label
  ctx.fillStyle = colors.text;
  ctx.font = '8px monospace';
  ctx.textAlign = 'right';
  ctx.textBaseline = 'middle';
  ctx.fillText(i.toString(), x - 8, ledY);
}

// Label at bottom
ctx.fillStyle = colors.text;
ctx.font = '8px monospace';
ctx.textAlign = 'center';
ctx.textBaseline = 'alphabetic';
ctx.fillText('BIN', x, y + height/2 - 2);
