logic.code:
const si = inputs[8].getValue();
const clock = inputs[9].getValue();
const load = inputs[10].getValue();
const dir = inputs[11].getValue();
if (clock && !state.lastClock) {
  if (load) {
    state.value = 0;
    for (let i = 0; i < 8; i++) {
      if (inputs[i].getValue()) state.value |= (1 << i);
    }
  } else {
    if (dir) {
      state.value = ((state.value << 1) | (si ? 1 : 0)) & 0xFF;
    } else {
      state.value = (state.value >> 1) | (si ? 0x80 : 0);
    }
  }
}
state.lastClock = clock;
for (let i = 0; i < 8; i++) {
  outputs[i].setValue((state.value >> i) & 1);
}
