let bcd = 0;
for (let i = 0; i < 4; i++) {
  if (inputs[i].getValue()) bcd |= (1 << i);
}
const segments = [
  0b1111110,
  0b0110000,
  0b1101101,
  0b1111001,
  0b0110011,
  0b1011011,
  0b1011111,
  0b1110000,
  0b1111111,
  0b1111011
];
const pattern = bcd < 10 ? segments[bcd] : 0;
for (let i = 0; i < 7; i++) {
  outputs[i].setValue((pattern >> i) & 1);
}
