logic.code:
const input = inputs[0].getValue();
state.buffer[state.index] = input;
const output = state.buffer[(state.index + 1) % state.delay];
state.index = (state.index + 1) % state.delay;
outputs[0].setValue(output);

rendering.customRender:
// Draw label (content only)
ctx.fillStyle = colors.text;
ctx.font = 'bold 10px monospace';
ctx.textAlign = 'center';
ctx.fillText('DELAY', x, y - 5);
ctx.font = '8px monospace';
ctx.fillText(state.delay + ' cycles', x, y + 6);
