logic.code:
const clk = inputs[0].getValue();
if (clk && !state.lastClk) {
  state.counter = (state.counter + 1) & 0xFF;
}
state.lastClk = clk;
let div = 0;
for (let i = 0; i < 3; i++) {
  if (inputs[i + 1].getValue()) div |= (1 << i);
}
const divider = Math.pow(2, div);
outputs[0].setValue((state.counter % divider) < (divider / 2));
