rendering.customRender:
// Draw ground symbol
ctx.strokeStyle = colors.wire;
ctx.lineWidth = 2;
ctx.beginPath();
ctx.moveTo(x, y - 10);
ctx.lineTo(x, y + 5);
ctx.stroke();

ctx.beginPath();
ctx.moveTo(x - 8, y + 5);
ctx.lineTo(x + 8, y + 5);
ctx.stroke();

ctx.lineWidth = 1.5;
ctx.beginPath();
ctx.moveTo(x - 6, y + 8);
ctx.lineTo(x + 6, y + 8);
ctx.stroke();

ctx.lineWidth = 1;
ctx.beginPath();
ctx.moveTo(x - 4, y + 11);
ctx.lineTo(x + 4, y + 11);
ctx.stroke();

ctx.fillStyle = colors.text;
ctx.font = '7px monospace';
ctx.textAlign = 'center';
ctx.fillText('GND', x, y - 12);
