const start = inputs[8].getValue();
if (start && !state.lastStart && !state.busy) {
  state.data = 0;
  for (let i = 0; i < 8; i++) {
    if (inputs[i].getValue()) state.data |= (1 << (7-i));
  }
  state.bitCount = 8;
  state.busy = true;
}
state.lastStart = start;
if (state.busy && state.bitCount > 0) {
  state.sclState = !state.sclState;
  if (!state.sclState) {
    state.data <<= 1;
    state.bitCount--;
    if (state.bitCount === 0) state.busy = false;
  }
}
outputs[0].setValue((state.data & 0x80) !== 0);
outputs[1].setValue(state.sclState);
outputs[2].setValue(state.busy);
