logic.code:
let priority = -1;
for (let i = 7; i >= 0; i--) {
  if (inputs[i].getValue()) {
    priority = i;
    break;
  }
}
if (priority >= 0) {
  outputs[0].setValue((priority & 1) !== 0);
  outputs[1].setValue((priority & 2) !== 0);
  outputs[2].setValue((priority & 4) !== 0);
  outputs[3].setValue(true);
} else {
  outputs[0].setValue(false);
  outputs[1].setValue(false);
  outputs[2].setValue(false);
  outputs[3].setValue(false);
}
