rendering.customRender:
// Draw probe indicator
const color = state.value ? '#4ec9b0' : '#666';
ctx.fillStyle = color;
ctx.beginPath();
ctx.arc(x, y, 10, 0, Math.PI * 2);
ctx.fill();

ctx.strokeStyle = colors.wire;
ctx.lineWidth = 2;
ctx.beginPath();
ctx.arc(x, y, 10, 0, Math.PI * 2);
ctx.stroke();

ctx.fillStyle = '#fff';
ctx.font = 'bold 10px monospace';
ctx.textAlign = 'center';
ctx.textBaseline = 'middle';
ctx.fillText(state.value ? '1' : '0', x, y);
