logic.code:
const clock = inputs[8].getValue();
const inc = inputs[9].getValue();
const load = inputs[10].getValue();
const rst = inputs[11].getValue();
if (rst) {
  state.pc = 0;
} else if (clock && !state.lastClock) {
  if (load) {
    state.pc = 0;
    for (let i = 0; i < 8; i++) {
      if (inputs[i].getValue()) state.pc |= (1 << i);
    }
  } else if (inc) {
    state.pc = (state.pc + 1) % 256;
  }
}
state.lastClock = clock;
for (let i = 0; i < 8; i++) {
  outputs[i].setValue((state.pc >> i) & 1);
}
