rendering.customRender:
// Draw resistor symbol
ctx.strokeStyle = colors.wire;
ctx.lineWidth = 2;
ctx.beginPath();
const zigzag = 4;
const spacing = 4;
for (let i = 0; i < 5; i++) {
  ctx.lineTo(x - 10 + i * spacing, y + (i % 2 === 0 ? -zigzag : zigzag));
}
ctx.stroke();

// Draw up arrow
ctx.fillStyle = '#4ec9b0';
ctx.beginPath();
ctx.moveTo(x, y - 12);
ctx.lineTo(x - 4, y - 8);
ctx.lineTo(x + 4, y - 8);
ctx.fill();

ctx.fillStyle = colors.text;
ctx.font = '8px monospace';
ctx.textAlign = 'center';
ctx.fillText('Râ†‘', x, y + height/2 - 3);
