rendering.customRender:
// Draw button
const pressed = state.pressed;
ctx.fillStyle = pressed ? '#4ec9b0' : '#2d2d30';
ctx.strokeStyle = colors.wire;
ctx.lineWidth = 2;
ctx.beginPath();
ctx.arc(x, y, 12, 0, Math.PI * 2);
ctx.fill();
ctx.stroke();

// Draw label
ctx.fillStyle = colors.text;
ctx.font = '8px monospace';
ctx.textAlign = 'center';
ctx.fillText('BTN', x, y + height/2 - 3);
