const clk = inputs[8].getValue();
if (clk && !state.lastClk) {
  state.counter = (state.counter + 1) % 200;
  state.angle = 0;
  for (let i = 0; i < 8; i++) {
    if (inputs[i].getValue()) state.angle |= (1 << i);
  }
}
state.lastClk = clk;
const pulseWidth = 10 + (state.angle * 80 / 255);
outputs[0].setValue(state.counter < pulseWidth);
