logic.code:
const clock = inputs[0].getValue();
const push = inputs[1].getValue();
const pop = inputs[2].getValue();
const rst = inputs[3].getValue();
if (rst) {
  state.sp = 255;
} else if (clock && !state.lastClock) {
  if (push) {
    state.sp = (state.sp - 1 + 256) % 256;
  } else if (pop) {
    state.sp = (state.sp + 1) % 256;
  }
}
state.lastClock = clock;
for (let i = 0; i < 8; i++) {
  outputs[i].setValue((state.sp >> i) & 1);
}
