const clk = inputs[0].getValue();
const dir = inputs[1].getValue();
const en = inputs[2].getValue();
if (clk && !state.lastClk && en) {
  if (dir) {
    state.step = (state.step + 1) % 4;
  } else {
    state.step = (state.step - 1 + 4) % 4;
  }
}
state.lastClk = clk;
const phases = [
  [true, false, false, false],
  [false, true, false, false],
  [false, false, true, false],
  [false, false, false, true]
];
for (let i = 0; i < 4; i++) {
  outputs[i].setValue(phases[state.step][i]);
}
