rendering.customRender:
// Draw switch
ctx.strokeStyle = state.closed ? '#4ec9b0' : colors.wire;
ctx.lineWidth = 3;
ctx.beginPath();
if (state.closed) {
  ctx.moveTo(x - 15, y);
  ctx.lineTo(x + 15, y);
} else {
  ctx.moveTo(x - 15, y);
  ctx.lineTo(x + 10, y - 12);
}
ctx.stroke();

// Draw terminals
ctx.fillStyle = colors.wire;
ctx.beginPath();
ctx.arc(x - 15, y, 2, 0, Math.PI * 2);
ctx.fill();
ctx.beginPath();
ctx.arc(x + 15, y, 2, 0, Math.PI * 2);
ctx.fill();

ctx.fillStyle = colors.text;
ctx.font = '9px monospace';
ctx.textAlign = 'center';
ctx.fillText(state.closed ? 'ON' : 'OFF', x, y + height/2 - 5);
