logic.code:
const clk = inputs[0].getValue();
const rst = inputs[1].getValue();
const en = inputs[2].getValue();
if (rst) {
  state.count = 0;
} else if (clk && !state.lastClk && en) {
  if (state.count < state.timeout) {
    state.count++;
  }
}
state.lastClk = clk;
const done = state.count >= state.timeout;
outputs[0].setValue(state.count > 0 && !done);
outputs[1].setValue(done);

rendering.customRender:
// Draw label and timeout (content only)
ctx.fillStyle = colors.text;
ctx.font = 'bold 9px monospace';
ctx.textAlign = 'center';
ctx.fillText('TIMER', x, y - 4);
ctx.font = '7px monospace';
ctx.fillText(state.count + '/' + state.timeout, x, y + 6);
