const rx = inputs[0].getValue();
const clk = inputs[1].getValue();
if (!rx && state.lastRx && state.bitCount === 0) {
  state.bitCount = 10;
  state.shiftReg = 0;
}
state.lastRx = rx;
if (clk && !state.lastClk && state.bitCount > 0) {
  state.shiftReg = (state.shiftReg >> 1) | (rx ? 0x200 : 0);
  state.bitCount--;
  if (state.bitCount === 0) {
    state.data = (state.shiftReg >> 1) & 0xFF;
    state.ready = true;
  }
}
state.lastClk = clk;
for (let i = 0; i < 8; i++) {
  outputs[i].setValue((state.data >> i) & 1);
}
outputs[8].setValue(state.ready);
