const clk = inputs[8].getValue();
const send = inputs[9].getValue();
if (send && !state.lastSend && !state.busy) {
  let data = 0;
  for (let i = 0; i < 8; i++) {
    if (inputs[i].getValue()) data |= (1 << i);
  }
  state.shiftReg = (data << 1) | 0x200;
  state.bitCount = 10;
  state.busy = true;
}
state.lastSend = send;
if (clk && !state.lastClk && state.busy) {
  state.bitCount--;
  if (state.bitCount === 0) {
    state.busy = false;
  }
}
state.lastClk = clk;
const tx = state.busy ? ((state.shiftReg >> (10 - state.bitCount)) & 1) : true;
outputs[0].setValue(tx);
outputs[1].setValue(state.busy);
