rendering.customRender:
// Draw VCC symbol
ctx.strokeStyle = '#4ec9b0';
ctx.lineWidth = 2;
ctx.beginPath();
ctx.moveTo(x, y + 10);
ctx.lineTo(x, y - 5);
ctx.stroke();

ctx.fillStyle = '#4ec9b0';
ctx.beginPath();
ctx.arc(x, y - 8, 3, 0, Math.PI * 2);
ctx.fill();

ctx.fillStyle = colors.text;
ctx.font = '7px monospace';
ctx.textAlign = 'center';
ctx.fillText('VCC', x, y + 14);
