logic.code:
const clk = inputs[0].getValue();
const kick = inputs[1].getValue();
const en = inputs[2].getValue();
if (kick) {
  state.counter = 0;
} else if (clk && !state.lastClk && en) {
  if (state.counter < state.timeout + 10) {
    state.counter++;
  }
}
state.lastClk = clk;
outputs[0].setValue(state.counter >= state.timeout);
